import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
// import socket from '../../services/socket';
import { io } from 'socket.io-client';

const socket = io(import.meta.env.VITE_BACKEND_URL);
socket.on('quiz_result', (data) => {
    console.log('New quiz result received:', data);
    // Optionally trigger a refresh or update your quiz results state here
});
const QuizResults = () => {
    const { quizId } = useParams();
    const { user } = useAuth();
    const [quiz, setQuiz] = useState(null);
    const [results, setResults] = useState([]);
    const [answerStats, setAnswerStats] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [sortBy, setSortBy] = useState('submitted_at');
    const [sortOrder, setSortOrder] = useState('DESC');

    useEffect(() => {
        if (quizId) {
            fetchQuizResults();
        }
    }, [quizId, sortBy, sortOrder]);

    useEffect(() => {
        const handleLiveResult = (newResult) => {
            if (newResult.quizId.toString() !== quizId.toString()) return;

            console.log('Live result received:', newResult);

            setResults(prevResults => {
                const exists = prevResults.some(r => r.id === newResult.id);
                if (exists) return prevResults; // Avoid duplicates
                return [newResult, ...prevResults];
            });
        };

        socket.on('quiz_result', handleLiveResult);

        return () => {
            socket.off('quiz_result', handleLiveResult);
        };
    }, [quizId]);

    const fetchQuizResults = async () => {
        try {
            setLoading(true);
            const response = await api.get(`/quizzes/${quizId}/results`);
            setQuiz(response.data.quiz);

            const sortedResults = response.data.results.sort((a, b) => {
                const aVal = a[sortBy];
                const bVal = b[sortBy];
                return sortOrder === 'ASC' ? (aVal > bVal ? 1 : -1) : (aVal < bVal ? 1 : -1);
            });

            setResults(sortedResults);

            const statsMap = new Map();
            response.data.answerStatistics.forEach(stat => {
                if (!statsMap.has(stat.question_id)) {
                    statsMap.set(stat.question_id, {
                        questionId: stat.question_id,
                        questionText: stat.question_text,
                        questionOrder: stat.question_order,
                        options: []
                    });
                }

                statsMap.get(stat.question_id).options.push({
                    optionId: stat.option_id,
                    optionText: stat.option_text,
                    isCorrect: stat.is_correct,
                    selectionCount: stat.selection_count,
                    percentage: stat.percentage
                });
            });

            setAnswerStats(Array.from(statsMap.values()).sort((a, b) => a.questionOrder - b.questionOrder));
            setError('');
        } catch (err) {
            console.error('QuizResults - Error fetching results:', err);
            setError(err.response?.data?.message || 'Failed to fetch quiz results');
        } finally {
            setLoading(false);
        }
    };

    const exportToCSV = async () => {
        try {
            const csvData = results.map(result => ({
                'Student Name': `${result.first_name} ${result.last_name}`,
                'Username': result.username,
                'Score': result.total_score,
                'Max Score': result.max_score,
                'Percentage': result.percentage,
                'Time Taken (minutes)': result.time_taken_minutes,
                'Started At': new Date(result.started_at).toLocaleString(),
                'Submitted At': new Date(result.submitted_at).toLocaleString()
            }));

            if (csvData.length === 0) {
                alert('No data to export');
                return;
            }

            const headers = Object.keys(csvData[0]);
            const csvContent = [
                headers.join(','),
                ...csvData.map(row => headers.map(field => `"${row[field]}"`).join(','))
            ].join('\n');

            const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
            const link = document.createElement('a');
            const url = URL.createObjectURL(blob);
            link.setAttribute('href', url);
            link.setAttribute('download', `quiz_${quizId}_results_${new Date().toISOString().slice(0, 10)}.csv`);
            link.style.visibility = 'hidden';
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);

            alert('Results exported successfully!');
        } catch (err) {
            console.error('QuizResults - Error exporting CSV:', err);
            alert('Failed to export results');
        }
    };

    const formatDate = (dateString) => new Date(dateString).toLocaleString();

    const getGradeBadgeClass = (percentage) => {
        if (percentage >= 90) return 'bg-success';
        if (percentage >= 80) return 'bg-info';
        if (percentage >= 70) return 'bg-warning';
        if (percentage >= 60) return 'bg-orange';
        return 'bg-danger';
    };

    const calculateOverallStats = () => {
        if (results.length === 0) return null;
        const totalStudents = results.length;
        const averageScore = results.reduce((sum, result) => sum + result.percentage, 0) / totalStudents;
        const highestScore = Math.max(...results.map(r => r.percentage));
        const lowestScore = Math.min(...results.map(r => r.percentage));
        const passRate = results.filter(r => r.percentage >= 60).length / totalStudents * 100;

        return {
            totalStudents,
            averageScore: Math.round(averageScore),
            highestScore,
            lowestScore,
            passRate: Math.round(passRate)
        };
    };

    const stats = calculateOverallStats();

    if (loading) {
        return <div className="container mt-4 text-center"><div className="spinner-border text-primary" role="status"></div></div>;
    }

    if (!quiz) {
        return <div className="container mt-4 alert alert-danger">Quiz not found or you don't have permission to view its results.</div>;
    }

    return (
        <div className="container mt-4">
            <div className="d-flex justify-content-between align-items-center mb-4">
                <div>
                    <h2>{quiz.title} - Results</h2>
                    <p className="text-muted mb-0">Total Submissions: {quiz.totalSubmissions}</p>
                </div>
                <div>
                    <button className="btn btn-success me-2" onClick={exportToCSV} disabled={results.length === 0}>
                        <i className="fas fa-download me-2"></i>Export CSV
                    </button>
                    <button className="btn btn-primary" onClick={fetchQuizResults}>
                        <i className="fas fa-sync-alt me-2"></i>Refresh
                    </button>
                </div>
            </div>

            {error && <div className="alert alert-danger">{error}</div>}

            {stats && (
                <div className="row mb-4">
                    <div className="col-md-2 mb-3"><div className="card text-center"><div className="card-body"><h5 className="card-title text-primary">{stats.totalStudents}</h5><p className="card-text small">Total Students</p></div></div></div>
                    <div className="col-md-2 mb-3"><div className="card text-center"><div className="card-body"><h5 className="card-title text-info">{stats.averageScore}%</h5><p className="card-text small">Average Score</p></div></div></div>
                    <div className="col-md-2 mb-3"><div className="card text-center"><div className="card-body"><h5 className="card-title text-success">{stats.highestScore}%</h5><p className="card-text small">Highest Score</p></div></div></div>
                    <div className="col-md-2 mb-3"><div className="card text-center"><div className="card-body"><h5 className="card-title text-danger">{stats.lowestScore}%</h5><p className="card-text small">Lowest Score</p></div></div></div>
                    <div className="col-md-2 mb-3"><div className="card text-center"><div className="card-body"><h5 className="card-title text-warning">{stats.passRate}%</h5><p className="card-text small">Pass Rate (â‰¥60%)</p></div></div></div>
                </div>
            )}

            <div className="card mb-4">
                <div className="card-header d-flex justify-content-between align-items-center">
                    <h5 className="mb-0">Student Results</h5>
                    <div className="d-flex">
                        <select className="form-select form-select-sm me-2" value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
                            <option value="submitted_at">Submission Date</option>
                            <option value="percentage">Score %</option>
                            <option value="total_score">Score Points</option>
                            <option value="time_taken_minutes">Time Taken</option>
                            <option value="last_name">Student Name</option>
                        </select>
                        <select className="form-select form-select-sm" value={sortOrder} onChange={(e) => setSortOrder(e.target.value)}>
                            <option value="DESC">Descending</option>
                            <option value="ASC">Ascending</option>
                        </select>
                    </div>
                </div>
                <div className="card-body">
                    {results.length === 0 ? (
                        <p className="text-muted text-center">No submissions yet for this quiz.</p>
                    ) : (
                        <div className="table-responsive">
                            <table className="table table-striped">
                                <thead>
                                    <tr>
                                        <th>Student</th>
                                        <th>Score</th>
                                        <th>Grade</th>
                                        <th>Time Taken</th>
                                        <th>Submitted At</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {results.map(result => (
                                        <tr key={result.id}>
                                            <td><strong>{result.first_name} {result.last_name}</strong><br /><small>{result.username}</small></td>
                                            <td>{result.total_score}/{result.max_score}</td>
                                            <td><span className={`badge ${getGradeBadgeClass(result.percentage)}`}>{result.percentage}%</span></td>
                                            <td>{result.time_taken_minutes} min</td>
                                            <td>{formatDate(result.submitted_at)}</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    )}
                </div>
            </div>

            {answerStats.length > 0 && (
                <div className="card">
                    <div className="card-header"><h5>Question Analysis & Poll Results</h5></div>
                    <div className="card-body">
                        {answerStats.map(question => (
                            <div key={question.questionId} className="mb-4">
                                <h6><strong>Question {question.questionOrder}:</strong> {question.questionText}</h6>
                                <div className="row">
                                    {question.options.map(option => (
                                        <div key={option.optionId} className="col-md-6 mb-2">
                                            <div className={`card ${option.isCorrect ? 'border-success' : ''}`}>
                                                <div className="card-body py-2">
                                                    <div className="d-flex justify-content-between align-items-center">
                                                        <small>{option.isCorrect && <i className="fas fa-check text-success me-1"></i>}{option.optionText}</small>
                                                        <div className="text-end">
                                                            <strong>{option.selectionCount} votes</strong><br />
                                                            <small>{option.percentage}%</small>
                                                        </div>
                                                    </div>
                                                    <div className="progress mt-1" style={{ height: '4px' }}>
                                                        <div className={`progress-bar ${option.isCorrect ? 'bg-success' : 'bg-primary'}`} style={{ width: `${option.percentage}%` }}></div>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            )}
        </div>
    );
};

export default QuizResults;
